import React, { useEffect, useRef, useState } from "react";
import Post from "../Posts/Post";
import "./PostInfo.css";
const PostInfo = () => {
  const postRefs = useRef([]);
  const [selectedTag, setSelectedTag] = useState("a");

  useEffect(() => {
    const handleScroll = () => {
      postRefs.current.forEach((post) => {
        if (post) {
          const rect = post.getBoundingClientRect();
          if (rect.top >= 0 && rect.bottom <= window.innerHeight) {
            post.classList.add("visible");
          }
        }
      });
    };

    window.addEventListener("scroll", handleScroll);
    handleScroll(); // Initial check for posts already in view

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const getPostsForSelectedTag = () => {
    switch (selectedTag) {
      case "a":
        return postsForTagA;
      case "b":
        return postsForTagB;
      case "c":
        return postsForTagC;
      default:
        return [];
    }
  };
  const tags = [
    { id: "a", label: "Tag A" },
    { id: "b", label: "Tag B" },
    { id: "c", label: "Tag C" },
  ];

  return (
    <div>
      <div className="tags">
        {tags.map((tag) => (
          <div
            key={tag.id}
            className={`tag ${selectedTag === tag.id ? "active" : ""}`}
            onClick={() => setSelectedTag(tag.id)}
          >
            {tag.label}
          </div>
        ))}
      </div>

      <div className="post">
        {getPostsForSelectedTag().map((post, index) => (
          <div
            key={index}
            ref={(el) => (postRefs.current[index] = el)}
            className="post-info"
          >
            <Post post={post} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default PostInfo;
